import React, { useState, useEffect } from 'react';
import { Box, Container, Typography, Card, CardContent, Grid, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, TextField, MenuItem } from '@mui/material';
import { Description, Event, Edit, Delete, Add } from '@mui/icons-material';

const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'];
const timeSlots = ['08:00-09:30', '09:30-11:00', '11:00-12:30', '12:30-14:00', '14:00-15:30', '15:30-17:00'];

const defaultTimetable = Array(days.length).fill().map(() => Array(timeSlots.length).fill({ subject: '', room: '', faculty: '' }));

const getInitialTimetable = () => {
  const saved = localStorage.getItem('studentTimetable');
  if (saved) return JSON.parse(saved);
  return defaultTimetable;
};

const Timetable = () => {
  const [timetable, setTimetable] = useState(getInitialTimetable());
  const [editCell, setEditCell] = useState(null); // { dayIndex, timeIndex }
  const [form, setForm] = useState({ subject: '', room: '', faculty: '' });

  useEffect(() => {
    localStorage.setItem('studentTimetable', JSON.stringify(timetable));
  }, [timetable]);

  const handleEdit = (dayIndex, timeIndex) => {
    setEditCell({ dayIndex, timeIndex });
    setForm(timetable[dayIndex][timeIndex]);
  };

  const handleDelete = (dayIndex, timeIndex) => {
    const updated = timetable.map((row, d) =>
      row.map((cell, t) => (d === dayIndex && t === timeIndex ? { subject: '', room: '', faculty: '' } : cell))
    );
    setTimetable(updated);
  };

  const handleDialogClose = () => {
    setEditCell(null);
    setForm({ subject: '', room: '', faculty: '' });
  };

  const handleFormChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSave = () => {
    const updated = timetable.map((row, d) =>
      row.map((cell, t) => (d === editCell.dayIndex && t === editCell.timeIndex ? form : cell))
    );
    setTimetable(updated);
    handleDialogClose();
  };

  return (
    <Box sx={{ background: 'linear-gradient(135deg, #e0eafc 0%, #cfdef3 100%)', minHeight: '100vh', py: 4 }}>
      <Container>
        <Typography variant="h4" component="h1" gutterBottom sx={{ fontWeight: 700, color: '#2d3a4b' }}>
          Time Scheduler
        </Typography>
        <Typography variant="subtitle1" color="text.secondary" sx={{ mb: 3 }}>
          Input and manage your weekly class schedule
        </Typography>
        <TableContainer component={Paper} sx={{ borderRadius: 3, boxShadow: 3 }}>
          <Table>
            <TableHead>
              <TableRow sx={{ background: 'linear-gradient(90deg, #a1c4fd 0%, #c2e9fb 100%)' }}>
                <TableCell sx={{ fontWeight: 700 }}>Time</TableCell>
                {days.map((day, index) => (
                  <TableCell key={index} align="center" sx={{ fontWeight: 700 }}>{day}</TableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
              {timeSlots.map((time, rowIndex) => (
                <TableRow key={rowIndex}>
                  <TableCell sx={{ fontWeight: 600 }}>{time}</TableCell>
                  {days.map((_, dayIndex) => (
                    <TableCell key={dayIndex} align="center" sx={{ position: 'relative', minWidth: 180, background: '#f8fafc' }}>
                      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 0.5, alignItems: 'center' }}>
                        <Typography variant="body2" color="text.primary" sx={{ fontWeight: 500 }}>
                          {timetable[dayIndex][rowIndex].subject || <span style={{ color: '#b0b0b0' }}>No Class</span>}
                        </Typography>
                        {timetable[dayIndex][rowIndex].room && (
                          <Typography variant="caption" color="text.secondary">
                            Room: {timetable[dayIndex][rowIndex].room}
                          </Typography>
                        )}
                        {timetable[dayIndex][rowIndex].faculty && (
                          <Typography variant="caption" color="text.secondary">
                            Faculty: {timetable[dayIndex][rowIndex].faculty}
                          </Typography>
                        )}
                        <Box sx={{ mt: 1 }}>
                          <IconButton size="small" color="primary" onClick={() => handleEdit(dayIndex, rowIndex)}>
                            <Edit fontSize="small" />
                          </IconButton>
                          <IconButton size="small" color="error" onClick={() => handleDelete(dayIndex, rowIndex)}>
                            <Delete fontSize="small" />
                          </IconButton>
                        </Box>
                      </Box>
                    </TableCell>
                  ))}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>

        {/* Edit Dialog */}
        <Dialog open={!!editCell} onClose={handleDialogClose}>
          <DialogTitle>Edit Schedule</DialogTitle>
          <DialogContent sx={{ minWidth: 350 }}>
            <TextField
              margin="dense"
              label="Subject"
              name="subject"
              value={form.subject}
              onChange={handleFormChange}
              fullWidth
              select
              sx={{ mb: 2 }}
            >
              <MenuItem value="">No Class</MenuItem>
              <MenuItem value="Mathematics">Mathematics</MenuItem>
              <MenuItem value="Physics">Physics</MenuItem>
              <MenuItem value="Chemistry">Chemistry</MenuItem>
              <MenuItem value="Computer Science">Computer Science</MenuItem>
              <MenuItem value="Electronics">Electronics</MenuItem>
              <MenuItem value="Lunch Break">Lunch Break</MenuItem>
            </TextField>
            <TextField
              margin="dense"
              label="Room"
              name="room"
              value={form.room}
              onChange={handleFormChange}
              fullWidth
              sx={{ mb: 2 }}
            />
            <TextField
              margin="dense"
              label="Faculty"
              name="faculty"
              value={form.faculty}
              onChange={handleFormChange}
              fullWidth
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleDialogClose}>Cancel</Button>
            <Button onClick={handleSave} variant="contained">Save</Button>
          </DialogActions>
        </Dialog>
      </Container>
    </Box>
  );
};

export default Timetable;
