import React, { useContext } from 'react';
import { EventContext } from './EventContext';
import { Box, Container, Typography, Grid, Card, CardContent } from '@mui/material';


const StudentProfile = () => {
  const { events } = useContext(EventContext);
  // Show all events (no date filter)
  const allEvents = events;

  // State for tasks
  const [tasks, setTasks] = React.useState([]);
  const [taskForm, setTaskForm] = React.useState({ name: '', duration: 1 });

  const handleTaskFormChange = (e) => {
    setTaskForm({ ...taskForm, [e.target.name]: e.target.value });
  };

  const handleAddTask = (e) => {
    e.preventDefault();
    if (!taskForm.name.trim() || !taskForm.duration) return;
    setTasks([...tasks, { name: taskForm.name.trim(), duration: Number(taskForm.duration) }]);
    setTaskForm({ name: '', duration: 1 });
  };

  const handleDeleteTask = (idx) => {
    setTasks(tasks.filter((_, i) => i !== idx));
  };

  return (
    <Box sx={{ minHeight: '100vh', background: 'linear-gradient(135deg, #e0eafc 0%, #cfdef3 100%)', py: 6 }}>
      <Container maxWidth="lg">
        <Box sx={{
          borderRadius: 5,
          boxShadow: 6,
          background: 'rgba(255,255,255,0.98)',
          px: { xs: 2, md: 6 },
          py: { xs: 3, md: 5 },
          mb: 4,
          textAlign: 'center',
        }}>
          <Typography variant="h3" component="h1" gutterBottom sx={{ fontWeight: 800, color: '#1a237e', letterSpacing: 1 }}>
            👤 Student Profile
          </Typography>
          <Typography variant="h6" color="text.secondary" sx={{ mb: 1, fontWeight: 500 }}>
            View your upcoming events
          </Typography>
        </Box>
        <Box sx={{ mb: 3 }}>
          <Typography variant="h4" gutterBottom sx={{ fontWeight: 700, color: '#3949ab', letterSpacing: 0.5 }}>
            📅 Upcoming Events
          </Typography>
        </Box>
        {allEvents.length === 0 ? (
          <Typography variant="body1" color="text.secondary" sx={{ fontSize: 18, textAlign: 'center', mt: 4 }}>
            No events available.
          </Typography>
        ) : (
          <Grid container spacing={4}>
            {[...allEvents].reverse().map((event) => (
              <Grid item xs={12} sm={6} md={4} key={event.id}>
                <Card sx={{
                  borderRadius: 4,
                  boxShadow: 4,
                  background: 'linear-gradient(120deg, #f8fafc 60%, #e3eafc 100%)',
                  transition: 'transform 0.2s',
                  '&:hover': { transform: 'translateY(-8px) scale(1.03)', boxShadow: 8 },
                }}>
                  <CardContent sx={{ p: 3 }}>
                    <Typography variant="h5" gutterBottom sx={{ fontWeight: 700, color: '#1976d2', mb: 1 }}>
                      {event.name}
                    </Typography>
                    <Typography color="text.secondary" sx={{ mb: 1, fontSize: 15 }}>
                      <strong>Date:</strong> {event.date ? new Date(event.date).toLocaleDateString() : ''}
                    </Typography>
                    <Typography color="text.secondary" sx={{ mb: 1, fontSize: 15 }}>
                      <strong>Description:</strong> {event.description}
                    </Typography>
                    <Typography color="text.secondary" sx={{ fontSize: 15 }}>
                      <strong>Location:</strong> {event.location}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}
      {/* Tasks to Schedule Section */}
      <Box sx={{ mt: 6, p: 4, borderRadius: 4, background: 'rgba(255,255,255,0.96)', boxShadow: 3 }}>
        <Typography variant="h5" sx={{ fontWeight: 700, color: '#1976d2', mb: 2 }}>
          Add Tasks to Schedule
        </Typography>
        <form onSubmit={handleAddTask}>
          <Grid container spacing={2} alignItems="center" sx={{ mb: 2 }}>
            <Grid item xs={12} sm={6} md={5}>
              <input
                type="text"
                name="name"
                value={taskForm.name}
                onChange={handleTaskFormChange}
                placeholder="Task Name"
                style={{ width: '100%', padding: 10, borderRadius: 6, border: '1px solid #b0bec5', fontSize: 16 }}
              />
            </Grid>
            <Grid item xs={8} sm={4} md={3}>
              <input
                type="number"
                name="duration"
                min={1}
                value={taskForm.duration}
                onChange={handleTaskFormChange}
                placeholder="Duration (slots)"
                style={{ width: '100%', padding: 10, borderRadius: 6, border: '1px solid #b0bec5', fontSize: 16 }}
              />
            </Grid>
            <Grid item xs={4} sm={2} md={2}>
              <button type="submit" style={{ width: '100%', padding: 10, borderRadius: 6, background: '#1976d2', color: '#fff', fontWeight: 600, fontSize: 16, border: 'none' }}>Add Task</button>
            </Grid>
          </Grid>
        </form>
        <Box>
          <Typography variant="subtitle1" sx={{ fontWeight: 600, mb: 1 }}>Tasks to Schedule:</Typography>
          <Grid container spacing={1}>
            {tasks.length === 0 ? (
              <Grid item xs={12}>
                <Typography color="text.secondary">No tasks added.</Typography>
              </Grid>
            ) : (
              tasks.map((task, idx) => (
                <Grid item xs={12} sm={6} md={4} key={idx}>
                  <Card sx={{ p: 2, display: 'flex', alignItems: 'center', justifyContent: 'space-between', borderRadius: 3, boxShadow: 2 }}>
                    <span><b>{task.name}</b> ({task.duration} slot{task.duration > 1 ? 's' : ''})</span>
                    <button onClick={() => handleDeleteTask(idx)} style={{ color: '#d32f2f', background: 'none', border: 'none', fontWeight: 600, cursor: 'pointer' }}>Delete</button>
                  </Card>
                </Grid>
              ))
            )}
          </Grid>
        </Box>
      </Box>
    </Container>
  </Box>
  );
};

export default StudentProfile;
