import React, { useState } from 'react';
import './AdminDashboard.css'; // Import the CSS file

// Custom Material-UI-like components
const Box = ({ children, sx = {}, className = '', ...props }) => {
  const styles = {
    ...sx,
    ...(sx.bgcolor && { backgroundColor: sx.bgcolor }),
    ...(sx.p && { padding: `${sx.p * 8}px` }),
    ...(sx.py && { paddingTop: `${sx.py * 8}px`, paddingBottom: `${sx.py * 8}px` }),
    ...(sx.mb && { marginBottom: `${sx.mb * 8}px` }),
  };
  
  return (
    <div style={styles} className={className} {...props}>
      {children}
    </div>
  );
};

const Container = ({ children, sx = {}, maxWidth = 'lg' }) => {
  const maxWidths = {
    sm: '600px',
    md: '960px',
    lg: '1280px',
    xl: '1920px'
  };
  
  const styles = {
    maxWidth: maxWidths[maxWidth],
    margin: '0 auto',
    padding: '0 24px',
    width: '100%',
    ...sx
  };
  
  return <div style={styles}>{children}</div>;
};

const Grid = ({ children, container, item, spacing = 0, xs, md, sx = {} }) => {
  if (container) {
    return (
      <div 
        className="grid-container"
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: `${spacing * 8}px`,
          ...sx
        }}
      >
        {children}
      </div>
    );
  }
  
  if (item) {
    return (
      <div className="grid-item" style={sx}>
        {children}
      </div>
    );
  }
  
  return <div style={sx}>{children}</div>;
};

const Typography = ({ children, variant = 'body1', component, gutterBottom, color, sx = {} }) => {
  const variants = {
    h1: { fontSize: '2.5rem', fontWeight: 300, lineHeight: 1.2 },
    h2: { fontSize: '2rem', fontWeight: 300, lineHeight: 1.2 },
    h3: { fontSize: '1.75rem', fontWeight: 400, lineHeight: 1.2 },
    h4: { fontSize: '1.5rem', fontWeight: 400, lineHeight: 1.235 },
    h5: { fontSize: '1.25rem', fontWeight: 400, lineHeight: 1.334 },
    h6: { fontSize: '1rem', fontWeight: 500, lineHeight: 1.6 },
    subtitle1: { fontSize: '1rem', fontWeight: 400, lineHeight: 1.75 },
    body1: { fontSize: '1rem', fontWeight: 400, lineHeight: 1.5 },
  };
  
  const colors = {
    'text.secondary': '#666666',
    primary: '#1976d2',
  };
  
  const Tag = component || (variant.startsWith('h') ? variant : 'p');
  
  const styles = {
    ...variants[variant],
    ...(gutterBottom && { marginBottom: '0.35em' }),
    ...(color && { color: colors[color] || color }),
    margin: variant.startsWith('h') ? '0 0 16px 0' : '0',
    ...sx
  };
  
  return <Tag style={styles}>{children}</Tag>;
};

const Card = ({ children, sx = {}, className = '', onMouseEnter, onMouseLeave }) => (
  <div 
    className={`mui-card ${className}`}
    style={{
      backgroundColor: '#fff',
      borderRadius: '12px',
      boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)',
      overflow: 'hidden',
      transition: 'all 0.3s ease',
      border: '1px solid #e5e7eb',
      ...sx
    }}
    onMouseEnter={onMouseEnter}
    onMouseLeave={onMouseLeave}
  >
    {children}
  </div>
);

const CardContent = ({ children, sx = {} }) => (
  <div style={{ padding: '24px', ...sx }}>
    {children}
  </div>
);

const CardActions = ({ children, sx = {} }) => (
  <div style={{ padding: '8px 24px 24px', ...sx }}>
    {children}
  </div>
);

const Button = ({ children, variant = 'text', color = 'primary', component = 'button', href, sx = {}, ...props }) => {
  const variants = {
    text: {
      backgroundColor: 'transparent',
      color: color === 'primary' ? '#1976d2' : '#666',
      border: 'none',
    },
    contained: {
      backgroundColor: color === 'primary' ? '#1976d2' : '#666',
      color: '#fff',
      border: 'none',
      boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
    },
    outlined: {
      backgroundColor: 'transparent',
      color: color === 'primary' ? '#1976d2' : '#666',
      border: `1px solid ${color === 'primary' ? '#1976d2' : '#666'}`,
    }
  };
  
  const baseStyles = {
    padding: '8px 16px',
    borderRadius: '6px',
    fontSize: '0.875rem',
    fontWeight: 500,
    textTransform: 'uppercase',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    textDecoration: 'none',
    display: 'inline-block',
    ...variants[variant],
    ...sx
  };
  
  if (component === 'a' && href) {
    return (
      <a 
        href={href} 
        style={baseStyles}
        className="mui-button"
        {...props}
      >
        {children}
      </a>
    );
  }
  
  return (
    <button 
      style={baseStyles}
      className="mui-button"
      {...props}
    >
      {children}
    </button>
  );
};

// Icon components
const Assignment = ({ sx = {} }) => (
  <svg style={{ fontSize: '40px', ...sx }} fill="currentColor" viewBox="0 0 24 24" width="40" height="40">
    <path d="M19 3h-4.18C14.4 1.84 13.3 1 12 1c-1.3 0-2.4.84-2.82 2H5c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2zm-7 0c.55 0 1 .45 1 1s-.45 1-1 1-1-.45-1-1 .45-1 1-1zm2 14H7v-2h7v2zm3-4H7v-2h10v2zm0-4H7V7h10v2z"/>
  </svg>
);

const Event = ({ sx = {} }) => (
  <svg style={{ fontSize: '40px', ...sx }} fill="currentColor" viewBox="0 0 24 24" width="40" height="40">
    <path d="M17 12h-5v5h5v-5zM16 1v2H8V1H6v2H5c-1.11 0-1.99.9-1.99 2L3 19c0 1.1.89 2 2 2h14c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2h-1V1h-2zm3 18H5V8h14v11z"/>
  </svg>
);

const Description = ({ sx = {} }) => (
  <svg style={{ fontSize: '40px', ...sx }} fill="currentColor" viewBox="0 0 24 24" width="40" height="40">
    <path d="M14,2H6A2,2 0 0,0 4,4V20A2,2 0 0,0 6,22H18A2,2 0 0,0 20,20V8L14,2M18,20H6V4H13V9H18V20Z"/>
  </svg>
);

const Receipt = ({ sx = {} }) => (
  <svg style={{ fontSize: '40px', ...sx }} fill="currentColor" viewBox="0 0 24 24" width="40" height="40">
    <path d="M18,17H6V5H18M18,3H6A2,2 0 0,0 4,5V17A2,2 0 0,0 6,19H18A2,2 0 0,0 20,17V5A2,2 0 0,0 18,3Z"/>
  </svg>
);

const Person = ({ sx = {} }) => (
  <svg style={{ fontSize: '40px', ...sx }} fill="currentColor" viewBox="0 0 24 24" width="40" height="40">
    <path d="M12,4A4,4 0 0,1 16,8A4,4 0 0,1 12,12A4,4 0 0,1 8,8A4,4 0 0,1 12,4M12,14C16.42,14 20,15.79 20,18V20H4V18C4,15.79 7.58,14 12,14Z"/>
  </svg>
);

const Group = ({ sx = {} }) => (
  <svg style={{ fontSize: '40px', ...sx }} fill="currentColor" viewBox="0 0 24 24" width="40" height="40">
    <path d="M16,4C16.88,4 17.67,4.38 18.22,5C18.78,4.38 19.56,4 20.44,4A2.56,2.56 0 0,1 23,6.56C23,7.25 22.22,8 21.33,8H18.67C17.78,8 17,7.25 17,6.56C17,5.38 17,5.38 17,5.38C17,4.62 16.38,4 15.56,4M12,6A3,3 0 0,1 15,9A3,3 0 0,1 12,12A3,3 0 0,1 9,9A3,3 0 0,1 12,6M6,8A2,2 0 0,1 8,10A2,2 0 0,1 6,12A2,2 0 0,1 4,10A2,2 0 0,1 6,8M12,15C16.42,15 20,16.79 20,19V21H4V19C4,16.79 7.58,15 12,15M6,14C8.67,14 10.67,14.67 10.67,15.33V16H1.33V15.33C1.33,14.67 3.33,14 6,14Z"/>
  </svg>
);

const DashboardAdmin = () => {
  const [hoveredCard, setHoveredCard] = useState(null);

  const quickActions = [
    {
      id: 1,
      icon: Assignment,
      title: "Manage Courses",
      description: "Add, edit, and manage courses",
      link: "/courses",
      color: "#1976d2",
      gradient: "linear-gradient(135deg, #1976d2 0%, #42a5f5 100%)"
    },
    {
      id: 2,
      icon: Event,
      title: "Manage Events",
      description: "Create and manage campus events",
      link: "/events",
      color: "#388e3c",
      gradient: "linear-gradient(135deg, #388e3c 0%, #66bb6a 100%)"
    },
    {
      id: 3,
      icon: Description,
      title: "Manage Complaints",
      description: "View and resolve student complaints",
      link: "/complaints/admin",
      color: "#f57c00",
      gradient: "linear-gradient(135deg, #f57c00 0%, #ffb74d 100%)"
    }
  ];

  const statistics = [
    {
      id: 1,
      icon: Person,
      count: "5,000+",
      label: "Students",
      color: "#e91e63",
      gradient: "linear-gradient(135deg, #e91e63 0%, #f06292 100%)"
    },
    {
      id: 2,
      icon: Group,
      count: "150+",
      label: "Courses",
      color: "#9c27b0",
      gradient: "linear-gradient(135deg, #9c27b0 0%, #ba68c8 100%)"
    },
    {
      id: 3,
      icon: Receipt,
      count: "50+",
      label: "Departments",
      color: "#ff5722",
      gradient: "linear-gradient(135deg, #ff5722 0%, #ff8a65 100%)"
    },
    {
      id: 4,
      icon: Event,
      count: "100+",
      label: "Events",
      color: "#607d8b",
      gradient: "linear-gradient(135deg, #607d8b 0%, #90a4ae 100%)"
    }
  ];

  return (
    <Box>
      {/* Enhanced Header */}
      <Box className="header-gradient" sx={{ p: 4, color: 'white' }}>
        <Container>
          <div className="floating-animation">
            <Typography variant="h4" component="h1" gutterBottom sx={{ fontWeight: 700, textShadow: '0 2px 4px rgba(0,0,0,0.3)' }}>
              ðŸš€ Admin Dashboard
            </Typography>
            <Typography variant="subtitle1" sx={{ opacity: 0.9, fontSize: '1.1rem' }}>
              Welcome back, <strong>Admin</strong>! Ready to manage your institution? âœ¨
            </Typography>
          </div>
        </Container>
      </Box>

      {/* Enhanced Quick Actions */}
      <Container sx={{ py: 6 }}>
        <Typography variant="h5" gutterBottom className="section-title" sx={{ fontWeight: 600, color: '#1a1a1a', mb: 4 }}>
          ðŸŽ¯ Quick Actions
        </Typography>
        <Grid container spacing={3}>
          {quickActions.map((action) => {
            const IconComponent = action.icon;
            return (
              <Grid item xs={12} md={4} key={action.id}>
                <Card
                  sx={{
                    height: '100%',
                    display: 'flex',
                    flexDirection: 'column',
                    background: hoveredCard === action.id ? 
                      `linear-gradient(135deg, ${action.color}15 0%, ${action.color}25 100%)` : 
                      'linear-gradient(135deg, #ffffff 0%, #f8fafc 100%)',
                    border: hoveredCard === action.id ? `2px solid ${action.color}` : '1px solid #e2e8f0',
                  }}
                  onMouseEnter={() => setHoveredCard(action.id)}
                  onMouseLeave={() => setHoveredCard(null)}
                >
                  <CardContent sx={{ flexGrow: 1, textAlign: 'center' }}>
                    <div 
                      className="icon-container pulse-animation"
                      style={{ background: action.gradient }}
                    >
                      <IconComponent sx={{ fontSize: 40, color: 'white' }} />
                    </div>
                    <Typography variant="h6" gutterBottom sx={{ fontWeight: 600, color: '#2d3748' }}>
                      {action.title}
                    </Typography>
                    <Typography color="text.secondary" sx={{ lineHeight: 1.6 }}>
                      {action.description}
                    </Typography>
                  </CardContent>
                  <CardActions sx={{ justifyContent: 'center', pb: 3 }}>
                    <Button 
                      component="a" 
                      href={action.link} 
                      variant="contained" 
                      sx={{
                        background: action.gradient,
                        fontWeight: 600,
                        px: 4,
                        py: 1.5,
                        borderRadius: 2,
                        textTransform: 'none',
                        fontSize: '1rem'
                      }}
                    >
                      {action.title}
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            );
          })}
        </Grid>
      </Container>

      {/* Enhanced Statistics */}
      <Box sx={{ bgcolor: '#f8fafc', py: 6 }}>
        <Container>
          <Typography variant="h5" gutterBottom className="section-title" sx={{ fontWeight: 600, color: '#1a1a1a', mb: 4 }}>
            ðŸ“Š Campus Statistics
          </Typography>
          <Grid container spacing={3}>
            {statistics.map((stat) => {
              const IconComponent = stat.icon;
              return (
                <Grid item xs={12} md={3} key={stat.id}>
                  <Card sx={{ 
                    textAlign: 'center', 
                    height: '100%',
                    background: 'linear-gradient(135deg, #ffffff 0%, #f1f5f9 100%)',
                    border: '1px solid #e2e8f0'
                  }}>
                    <CardContent sx={{ py: 4 }}>
                      <div 
                        className="icon-container floating-animation"
                        style={{ 
                          background: stat.gradient,
                          margin: '0 auto 24px'
                        }}
                      >
                        <IconComponent sx={{ fontSize: 40, color: 'white' }} />
                      </div>
                      <div className="stat-number">
                        {stat.count}
                      </div>
                      <Typography 
                        color="text.secondary" 
                        sx={{ 
                          fontWeight: 500, 
                          fontSize: '1.1rem',
                          textTransform: 'uppercase',
                          letterSpacing: '0.5px'
                        }}
                      >
                        {stat.label}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              );
            })}
          </Grid>
        </Container>
      </Box>
    </Box>
  );
};

export default DashboardAdmin;
