import React, { useEffect, useState } from 'react';
import { Box, Container, Grid, Typography, Card, CardContent, CardActions, Button, Avatar } from '@mui/material';
import { Assignment, Event, Description, Receipt, Person, Group } from '@mui/icons-material';

const DashboardStudent = () => {
  const [student, setStudent] = useState({ name: '', email: '' });

  useEffect(() => {
    // Example: login details stored as { name, email } in localStorage under 'studentLogin'
    const stored = localStorage.getItem('studentLogin');
    if (stored) {
      try {
        setStudent(JSON.parse(stored));
      } catch {
        setStudent({ name: '', email: '' });
      }
    }
  }, []);

  return (
    <Box>
      {/* Header with Profile Icon */}
      <Box sx={{ bgcolor: '#f5f5f5', p: 3 }}>
        <Container sx={{ display: 'flex', alignItems: 'center', gap: 3 }}>
          <Avatar sx={{ width: 64, height: 64, bgcolor: '#1976d2', fontSize: 36 }}>
            <Person fontSize="inherit" />
          </Avatar>
          <Box>
            <Typography variant="h4" component="h1" gutterBottom>
              Student Dashboard
            </Typography>
            <Typography variant="subtitle1" color="text.secondary">
              Welcome, {student.name ? student.name : '[Student Name]'}!
            </Typography>
            {student.email && (
              <Typography variant="body2" color="text.secondary" sx={{ mt: 0.5 }}>
                <strong>Email:</strong> {student.email}
              </Typography>
            )}
          </Box>
        </Container>
      </Box>

      {/* ...existing code... */}

      {/* Recent Announcements */}
      <Container sx={{ py: 4 }}>
        <Typography variant="h5" gutterBottom>
          Recent Announcements
        </Typography>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  Academic Calendar Update
                </Typography>
                <Typography color="text.secondary">
                  The academic calendar for the next semester has been updated with new dates and important deadlines.
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  New Library Hours
                </Typography>
                <Typography color="text.secondary">
                  The library will have extended hours during exam week. Check the new schedule on the announcements page.
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default DashboardStudent;
