import React, { useState, useContext } from 'react';
import { EventContext } from './EventContext';
import './AdminDashboard.css';
import { Box, Container, Typography, Button, Grid } from '@mui/material';

const ManageEvents = () => {
  const { events, addEvent } = useContext(EventContext);
  const [eventData, setEventData] = useState({
    name: '',
    date: '',
    description: '',
    location: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEventData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addEvent(eventData);
    alert('Event created successfully!'); // Debug: confirm event creation
    setEventData({ name: '', date: '', description: '', location: '' });
  };

  return (
    <Box className="dashboard-wrapper">
      <Box className="header-gradient">
        <Container maxWidth="lg">
          <div className="floating-animation">
            <Typography variant="h4" component="h1" gutterBottom className="header-title">
              ðŸŽ‰ Manage Events
            </Typography>
            <Typography variant="subtitle1" className="header-subtitle">
              Create and manage campus events
            </Typography>
          </div>
        </Container>
      </Box>
      <Container maxWidth="lg" className="event-form-section">
        <Typography variant="h5" gutterBottom className="section-title">
          Create New Event
        </Typography>
        <form onSubmit={handleSubmit} className="modal-form">
          <div className="form-group">
            <label className="form-label">Event Name</label>
            <input
              type="text"
              name="name"
              value={eventData.name}
              onChange={handleInputChange}
              required
              className="form-input"
              placeholder="Enter event name"
            />
          </div>
          <div className="form-group">
            <label className="form-label">Date</label>
            <input
              type="date"
              name="date"
              value={eventData.date}
              onChange={handleInputChange}
              required
              className="form-input"
            />
          </div>
          <div className="form-group">
            <label className="form-label">Description</label>
            <textarea
              name="description"
              value={eventData.description}
              onChange={handleInputChange}
              required
              className="form-input"
              placeholder="Enter event description"
            />
          </div>
          <div className="form-group">
            <label className="form-label">Location</label>
            <input
              type="text"
              name="location"
              value={eventData.location}
              onChange={handleInputChange}
              required
              className="form-input"
              placeholder="Enter event location"
            />
          </div>
          <div className="form-actions">
            <Button
              type="submit"
              variant="contained"
              color="primary"
              className="submit-button gradient-primary"
            >
              Create Event
            </Button>
          </div>
        </form>
      </Container>
      {/* Professional Event List for Admins */}
      <Container maxWidth="lg" sx={{ mt: 6 }}>
        <Typography variant="h5" gutterBottom className="section-title" sx={{ fontWeight: 700, color: '#1976d2', mb: 3 }}>
          All Posted Events
        </Typography>
        {events.length === 0 ? (
          <Typography color="text.secondary" sx={{ fontSize: 18, textAlign: 'center', mt: 2 }}>
            No events have been posted yet.
          </Typography>
        ) : (
          <Grid container spacing={4}>
            {[...events].reverse().map((event) => (
              <Grid item xs={12} sm={6} md={4} key={event.id}>
                <Box sx={{
                  borderRadius: 4,
                  boxShadow: 4,
                  background: 'linear-gradient(120deg, #f8fafc 60%, #e3eafc 100%)',
                  transition: 'transform 0.2s',
                  '&:hover': { transform: 'translateY(-8px) scale(1.03)', boxShadow: 8 },
                  p: 3,
                  mb: 2,
                  border: '1px solid #e3eafc',
                }}>
                  <Typography variant="h6" sx={{ fontWeight: 700, color: '#1976d2', mb: 1 }}>
                    {event.name}
                  </Typography>
                  <Typography color="text.secondary" sx={{ mb: 1, fontSize: 15 }}>
                    <strong>Date:</strong> {event.date ? new Date(event.date).toLocaleDateString() : ''}
                  </Typography>
                  <Typography color="text.secondary" sx={{ mb: 1, fontSize: 15 }}>
                    <strong>Description:</strong> {event.description}
                  </Typography>
                  <Typography color="text.secondary" sx={{ fontSize: 15 }}>
                    <strong>Location:</strong> {event.location}
                  </Typography>
                </Box>
              </Grid>
            ))}
          </Grid>
        )}
      </Container>
    </Box>
  );
};

export default ManageEvents;
