import React, { useState, useEffect } from 'react';

/* ---------- Enhanced Student Dashboard Styles ---------- */
const styles = {
  dashboardWrapper: {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #FF9587 0%, #FFECCC 100%)',
    fontFamily: '"Inter", "Segoe UI", Tahoma, Geneva, Verdana, sans-serif',
    padding: '2rem',
  },

  // Header Styles
  header: {
    background: 'linear-gradient(135deg, #FF9587 0%, #FFECCC 100%)',
    borderRadius: '24px',
    padding: '3rem 2rem',
    color: '#fff',
    marginBottom: '2rem',
    position: 'relative',
    overflow: 'hidden',
    boxShadow: '0 20px 40px rgba(255, 149, 135, 0.2)',
  },

  headerBackground: {
    position: 'absolute',
    top: 0,
    right: 0,
    width: '300px',
    height: '300px',
    background: 'radial-gradient(circle, #FFECCC 0%, transparent 70%)',
    borderRadius: '50%',
    transform: 'translate(100px, -100px)',
  },

  headerContent: {
    position: 'relative',
    zIndex: 2,
  },

  headerTitle: {
    fontSize: '2.5rem',
    fontWeight: '700',
    marginBottom: '0.5rem',
    textShadow: '2px 2px 4px rgba(0,0,0,0.1)',
  },

  headerSubtitle: {
    fontSize: '1.2rem',
    opacity: 0.9,
    fontWeight: '400',
  },

  headerTime: {
    position: 'absolute',
    top: '2rem',
    right: '2rem',
    background: 'rgba(255, 236, 204, 0.7)',
    color: '#FF9587',
    padding: '0.5rem 1rem',
    borderRadius: '12px',
    backdropFilter: 'blur(10px)',
    fontSize: '0.9rem',
    fontWeight: '600',
  },

  // Stats Cards
  statsContainer: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
    gap: '1rem',
    marginBottom: '2rem',
  },

  statCard: {
    background: '#FFECCC',
    borderRadius: '16px',
    padding: '1.5rem',
    boxShadow: '0 4px 20px rgba(255,149,135,0.08)',
    border: '1px solid #FF9587',
    transition: 'all 0.3s ease',
    cursor: 'pointer',
  },

  statCardHover: {
    transform: 'translateY(-4px)',
    boxShadow: '0 8px 30px rgba(255,149,135,0.12)',
  },

  statNumber: {
    fontSize: '2rem',
    fontWeight: '700',
    color: '#FF9587',
    marginBottom: '0.5rem',
  },

  statLabel: {
    fontSize: '0.9rem',
    color: '#FF9587',
    fontWeight: '500',
  },

  statIcon: {
    width: '40px',
    height: '40px',
    borderRadius: '10px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '1.2rem',
    marginBottom: '1rem',
    background: '#FF9587',
    color: '#FFECCC',
  },

  // Quick Actions
  quickActions: {
    marginBottom: '2rem',
  },

  sectionTitle: {
    fontSize: '1.5rem',
    fontWeight: '700',
    color: '#1e293b',
    marginBottom: '1.5rem',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
  },

  quickActionsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
    gap: '1.5rem',
  },

  quickActionCard: {
    background: '#FFECCC',
    borderRadius: '20px',
    padding: '2rem',
    boxShadow: '0 8px 25px rgba(255,149,135,0.08)',
    border: '1px solid #FF9587',
    transition: 'all 0.3s ease',
    position: 'relative',
    overflow: 'hidden',
  },

  quickActionCardHover: {
    transform: 'translateY(-8px)',
    boxShadow: '0 20px 40px rgba(255,149,135,0.15)',
  },

  quickActionIcon: {
    width: '60px',
    height: '60px',
    borderRadius: '16px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '1.5rem',
    marginBottom: '1rem',
    background: '#FF9587',
    color: '#FFECCC',
  },

  quickActionTitle: {
    fontSize: '1.2rem',
    fontWeight: '600',
    color: '#FF9587',
    marginBottom: '0.5rem',
  },

  quickActionDesc: {
    fontSize: '0.9rem',
    color: '#FF9587',
    lineHeight: 1.5,
    marginBottom: '1.5rem',
  },

  quickActionBtn: {
    background: 'linear-gradient(135deg, #FF9587 0%, #FFECCC 100%)',
    color: '#fff',
    border: 'none',
    borderRadius: '12px',
    padding: '0.75rem 1.5rem',
    fontSize: '0.9rem',
    fontWeight: '600',
    cursor: 'pointer',
    textDecoration: 'none',
    display: 'inline-block',
    transition: 'all 0.3s ease',
    boxShadow: '0 4px 12px rgba(255, 149, 135, 0.3)',
  },

  quickActionBtnHover: {
    transform: 'translateY(-2px)',
    boxShadow: '0 8px 20px rgba(255, 149, 135, 0.4)',
  },

  // Content Grid
  contentGrid: {
    display: 'grid',
    gridTemplateColumns: '2fr 1fr',
    gap: '2rem',
    marginBottom: '2rem',
  },

  // Announcements
  announcements: {
    background: '#FFECCC',
    borderRadius: '20px',
    padding: '2rem',
    boxShadow: '0 8px 25px rgba(255,149,135,0.08)',
    border: '1px solid #FF9587',
  },

  announcementCard: {
    background: 'linear-gradient(135deg, #FFECCC 0%, #FF9587 100%)',
    borderRadius: '12px',
    padding: '1.5rem',
    marginBottom: '1rem',
    border: '1px solid #FF9587',
