import React, { useState, useContext } from 'react';
import { EventContext } from './EventContext';
import './AdminDashboard.css';
import { Box, Container, Typography, Button, Grid } from '@mui/material';

const ManageEvents = () => {
  const { events, addEvent } = useContext(EventContext);
  const [eventData, setEventData] = useState({
    name: '',
    date: '',
    location: '',
    description: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEventData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!eventData.name || !eventData.date || !eventData.location || !eventData.description) {
      alert('Please fill in all fields');
      return;
    }
    addEvent(eventData);
    setEventData({ name: '', date: '', location: '', description: '' });
  };

  return (
    <Box sx={{ minHeight: '100vh', background: 'linear-gradient(135deg, #e0eafc 0%, #e3eafc 100%)', py: 6 }}>
      {/* Hero Section */}
      <Container maxWidth="lg" sx={{ mb: 6 }}>
        <Box sx={{
          borderRadius: 5,
          boxShadow: 6,
          background: 'rgba(255,255,255,0.98)',
          px: { xs: 2, md: 6 },
          py: { xs: 3, md: 5 },
          mb: 4,
          textAlign: 'center',
        }}>
          <Typography variant="h3" component="h1" gutterBottom sx={{ fontWeight: 800, color: '#1a237e', letterSpacing: 1 }}>
            🎉 Manage Events
          </Typography>
          <Typography variant="h6" color="text.secondary" sx={{ mb: 1, fontWeight: 500 }}>
            Create, view, and manage all campus events in one place
          </Typography>
        </Box>
        {/* Event Form */}
        <Box sx={{ background: 'linear-gradient(120deg, #f8fafc 60%, #e3eafc 100%)', borderRadius: 4, boxShadow: 3, p: 4, mb: 6 }}>
          <Typography variant="h5" gutterBottom sx={{ fontWeight: 700, color: '#3949ab', mb: 2 }}>
            Create New Event
          </Typography>
          <form onSubmit={handleSubmit} className="modal-form">
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6} md={3}>
                <input
                  type="text"
                  name="name"
                  value={eventData.name}
                  onChange={handleInputChange}
                  required
                  className="form-input"
                  placeholder="Event Name"
                  style={{ width: '100%', padding: 12, borderRadius: 8, border: '1px solid #e3eafc', fontSize: 16 }}
                />
              </Grid>
              <Grid item xs={12} sm={6} md={3}>
                <input
                  type="date"
                  name="date"
                  value={eventData.date}
                  onChange={handleInputChange}
                  required
                  className="form-input"
                  style={{ width: '100%', padding: 12, borderRadius: 8, border: '1px solid #e3eafc', fontSize: 16 }}
                />
              </Grid>
              <Grid item xs={12} sm={6} md={3}>
                <input
                  type="text"
                  name="location"
                  value={eventData.location}
                  onChange={handleInputChange}
                  required
                  className="form-input"
                  placeholder="Location"
                  style={{ width: '100%', padding: 12, borderRadius: 8, border: '1px solid #e3eafc', fontSize: 16 }}
                />
              </Grid>
              <Grid item xs={12} sm={6} md={3}>
                <input
                  type="text"
                  name="description"
                  value={eventData.description}
                  onChange={handleInputChange}
                  required
                  className="form-input"
                  placeholder="Description"
                  style={{ width: '100%', padding: 12, borderRadius: 8, border: '1px solid #e3eafc', fontSize: 16 }}
                />
              </Grid>
              <Grid item xs={12}>
                <Button
                  type="submit"
                  variant="contained"
                  color="primary"
                  className="submit-button gradient-primary"
                  sx={{ fontWeight: 700, fontSize: 18, px: 4, borderRadius: 3 }}
                >
                  Create Event
                </Button>
              </Grid>
            </Grid>
          </form>
        </Box>
      </Container>
      {/* Professional Event List for Admins */}
      <Container maxWidth="lg" sx={{ mt: 2 }}>
        <Typography variant="h4" gutterBottom sx={{ fontWeight: 800, color: '#1976d2', mb: 4, letterSpacing: 0.5 }}>
          All Posted Events
        </Typography>
        {events.length === 0 ? (
          <Typography color="text.secondary" sx={{ fontSize: 18, textAlign: 'center', mt: 2 }}>
            No events have been posted yet.
          </Typography>
        ) : (
          <Grid container spacing={4}>
            {[...events].reverse().map((event) => (
              <Grid item xs={12} sm={6} md={4} key={event.id}>
                <Box
                  sx={{
                    borderRadius: 5,
                    boxShadow: 6,
                    background: 'linear-gradient(120deg, #f8fafc 60%, #e3eafc 100%)',
                    transition: 'transform 0.2s',
                    p: 4,
                    mb: 2,
                    border: '1px solid #e3eafc',
                    minHeight: 210,
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'center',
                    '&:hover': {
                      transform: 'translateY(-8px) scale(1.03)',
                      boxShadow: 8,
                    }
                  }}
                >
                  <Typography variant="h5" sx={{ fontWeight: 700, color: '#1976d2', mb: 1, letterSpacing: 0.5 }}>
                    {event.name}
                  </Typography>
                  <Typography color="text.secondary" sx={{ mb: 1, fontSize: 16 }}>
                    <strong>Date:</strong> {event.date ? new Date(event.date).toLocaleDateString() : ''}
                  </Typography>
                  <Typography color="text.secondary" sx={{ mb: 1, fontSize: 16 }}>
                    <strong>Description:</strong> {event.description}
                  </Typography>
                  <Typography color="text.secondary" sx={{ fontSize: 16 }}>
                    <strong>Location:</strong> {event.location}
                  </Typography>
                </Box>
              </Grid>
            ))}
          </Grid>
        )}
      </Container>
    </Box>
  );
};

export default ManageEvents;
