import React, { useState, useEffect } from 'react';

const AdminDashboard = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  return (
    <div className="min-h-screen flex bg-gradient-to-br from-slate-50 to-blue-50">
      {/* Sidebar */}
      <div className={`fixed lg:static inset-y-0 left-0 z-50 w-64 bg-white shadow-2xl transform transition-transform duration-300 ease-in-out ${
        isSidebarOpen ? 'translate-x-0' : '-translate-x-full lg:translate-x-0'
      }`}>
        <div className="p-6 border-b border-gray-100">
          <h2 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
            EduAdmin
          </h2>
          <p className="text-sm text-gray-500 mt-1">Management Portal</p>
        </div>
        <nav className="mt-6 px-3">
          <SidebarItem icon={HomeIcon} label="Dashboard" active />
          <SidebarItem icon={BookIcon} label="Courses" />
          <SidebarItem icon={UserGroupIcon} label="Students" />
          <SidebarItem icon={CalendarIcon} label="Events" />
          <SidebarItem icon={ExclamationIcon} label="Complaints" badge="3" />
          <SidebarItem icon={UserIcon} label="Profile" />
          <SidebarItem icon={CogIcon} label="Settings" />
        </nav>
      </div>

      {/* Overlay for mobile */}
      {isSidebarOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden"
          onClick={() => setIsSidebarOpen(false)}
        />
      )}

      {/* Main Content */}
      <div className="flex-1 lg:ml-0">
        {/* Header */}
        <Header onMenuClick={() => setIsSidebarOpen(!isSidebarOpen)} />

        {/* Welcome Banner */}
        <div className="relative bg-gradient-to-r from-blue-600 via-indigo-600 to-purple-700 text-white overflow-hidden">
          <div className="absolute inset-0 bg-black opacity-10"></div>
          <div className="absolute inset-0 bg-gradient-to-br from-transparent via-white/5 to-transparent"></div>
          <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
            <div className="animate-fadeInUp">
              <h2 className="text-3xl font-bold mb-2">Welcome back, Admin! ðŸ‘‹</h2>
              <p className="text-blue-100 text-lg">Here's what's happening with your institution today.</p>
            </div>
            <div className="absolute top-0 right-0 w-32 h-32 bg-white/10 rounded-full -mr-16 -mt-16 animate-pulse"></div>
            <div className="absolute bottom-0 left-0 w-24 h-24 bg-white/5 rounded-full -ml-12 -mb-12 animate-pulse delay-1000"></div>
          </div>
        </div>

        {/* Dashboard Content */}
        <DashboardContent />
      </div>
    </div>
  );
};

const SidebarItem = ({ icon: Icon, label, active = false, badge }) => (
  <a
    href="#"
    className={`group flex items-center px-4 py-3 mb-2 text-sm font-medium rounded-xl transition-all duration-200 transform hover:scale-105 ${
      active 
        ? 'bg-gradient-to-r from-blue-500 to-indigo-600 text-white shadow-lg shadow-blue-500/25' 
        : 'text-gray-600 hover:bg-gradient-to-r hover:from-blue-50 hover:to-indigo-50 hover:text-indigo-700'
    }`}
  >
    <Icon className={`w-5 h-5 mr-3 transition-colors ${active ? 'text-white' : 'text-gray-400 group-hover:text-indigo-500'}`} />
    <span className="flex-1">{label}</span>
    {badge && (
      <span className="flex items-center justify-center w-5 h-5 bg-red-500 text-white text-xs rounded-full animate-bounce">
        {badge}
      </span>
    )}
  </a>
);

const Header = ({ onMenuClick }) => (
  <header className="bg-white/80 backdrop-blur-md shadow-sm border-b border-gray-100 sticky top-0 z-30">
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex items-center justify-between">
      <div className="flex items-center">
        <button 
          onClick={onMenuClick}
          className="lg:hidden mr-4 p-2 rounded-lg text-gray-500 hover:bg-gray-100 transition-colors"
        >
          <MenuIcon className="w-6 h-6" />
        </button>
        <div>
          <h1 className="text-2xl font-bold text-gray-800">Admin Dashboard</h1>
          <p className="text-sm text-gray-500">Manage your institution efficiently</p>
        </div>
      </div>
      <div className="flex items-center space-x-4">
        <NotificationIcon />
        <ProfileIcon />
      </div>
    </div>
  </header>
);

const NotificationIcon = () => (
  <button className="relative p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg transition-all duration-200 hover:scale-110">
    <BellIcon className="w-6 h-6" />
    <span className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 rounded-full animate-ping"></span>
    <span className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 rounded-full"></span>
  </button>
);

const ProfileIcon = () => (
  <div className="relative group">
    <img
      className="h-10 w-10 rounded-full object-cover ring-2 ring-blue-500 ring-offset-2 transition-transform duration-200 group-hover:scale-110 cursor-pointer"
      src="https://via.placeholder.com/40"
      alt="User profile"
    />
    <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-400 rounded-full border-2 border-white"></div>
  </div>
);

const DashboardContent = () => (
  <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
      <DashboardCard 
        title="Manage Courses" 
        desc="Add, edit, and manage courses" 
        link="#" 
        icon="ðŸ“š"
        gradient="from-blue-500 to-blue-600"
      />
      <DashboardCard 
        title="Manage Events" 
        desc="Create and manage campus events" 
        link="#" 
        icon="ðŸ“…"
        gradient="from-green-500 to-green-600"
      />
      <DashboardCard 
        title="Quick Actions" 
        desc="Manage common tasks" 
        link="#" 
        isQuick 
        icon="âš¡"
        gradient="from-purple-500 to-purple-600"
      />
      <DashboardCard 
        title="Manage Complaints" 
        desc="View and resolve student complaints" 
        link="#" 
        icon="â—"
        gradient="from-red-500 to-red-600"
      />
      <DashboardCard 
        title="Student Registration" 
        desc="Register new students to the system" 
        link="#" 
        icon="ðŸ‘¥"
        gradient="from-indigo-500 to-indigo-600"
      />
      <DashboardCard 
        title="Reports & Analytics" 
        desc="View detailed reports and statistics" 
        link="#" 
        icon="ðŸ“Š"
        gradient="from-orange-500 to-orange-600"
      />
    </div>
    <QuickStats />
  </div>
);

const DashboardCard = ({ title, desc, link, isQuick, icon, gradient }) => (
  <div className="group bg-white rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-300 transform hover:-translate-y-2 overflow-hidden border border-gray-100">
    <div className={`h-2 bg-gradient-to-r ${gradient}`}></div>
    <div className="p-6">
      <div className="flex items-center mb-4">
        <div className={`w-12 h-12 rounded-xl bg-gradient-to-r ${gradient} flex items-center justify-center text-white text-xl shadow-lg`}>
          {icon}
        </div>
        <div className="ml-4">
          <h3 className="text-xl font-bold text-gray-800 group-hover:text-gray-900">{title}</h3>
        </div>
      </div>
      <p className="text-gray-600 mb-6 leading-relaxed">{desc}</p>
      {isQuick ? (
        <div className="flex flex-wrap gap-3">
          <button className="px-4 py-2 bg-gray-100 text-gray-700 font-medium rounded-lg hover:bg-gray-200 transition-colors hover:scale-105 transform">
            View Announcements
          </button>
          <button className="px-4 py-2 bg-gray-100 text-gray-700 font-medium rounded-lg hover:bg-gray-200 transition-colors hover:scale-105 transform">
            Register Student
          </button>
        </div>
      ) : (
        <button className={`w-full px-6 py-3 bg-gradient-to-r ${gradient} text-white font-semibold rounded-xl hover:shadow-lg transition-all duration-200 transform hover:scale-105`}>
          Open {title}
        </button>
      )}
    </div>
  </div>
);

const QuickStats = () => (
  <div className="mt-12">
    <div className="flex items-center justify-between mb-8">
      <h3 className="text-2xl font-bold text-gray-800">Quick Statistics</h3>
      <button className="text-blue-600 hover:text-blue-700 font-medium">View All â†’</button>
    </div>
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
      <StatCard title="Total Students" count="2,453" icon="ðŸ‘¨â€ðŸŽ“" color="blue" trend="+12%" />
      <StatCard title="Active Courses" count="42" icon="ðŸ“–" color="green" trend="+3%" />
      <StatCard title="Open Complaints" count="7" icon="âš ï¸" color="red" trend="-2%" />
      <StatCard title="Upcoming Events" count="3" icon="ðŸŽ‰" color="purple" trend="+1%" />
    </div>
  </div>
);

const StatCard = ({ title, count, icon, color, trend }) => {
  const colorClasses = {
    blue: 'from-blue-500 to-blue-600',
    green: 'from-green-500 to-green-600',
    red: 'from-red-500 to-red-600',
    purple: 'from-purple-500 to-purple-600'
  };

  return (
    <div className="bg-white p-6 rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1 border border-gray-100">
      <div className="flex items-center justify-between">
        <div className={`w-14 h-14 rounded-2xl bg-gradient-to-r ${colorClasses[color]} flex items-center justify-center text-white text-2xl shadow-lg`}>
          {icon}
        </div>
        <div className={`text-sm font-semibold px-2 py-1 rounded-full ${
          trend.startsWith('+') ? 'text-green-600 bg-green-100' : 'text-red-600 bg-red-100'
        }`}>
          {trend}
        </div>
      </div>
      <div className="mt-4">
        <p className="text-sm font-medium text-gray-500 mb-1">{title}</p>
        <p className="text-3xl font-bold text-gray-800">{count}</p>
      </div>
    </div>
  );
};

// Enhanced Icons with better styling
const HomeIcon = ({ className }) => (
  <svg className={className} fill="currentColor" viewBox="0 0 20 20">
    <path d="M10.707 2.293a1 1 0 00-1.414 0l-7 7a1 1 0 001.414 1.414L4 10.414V17a1 1 0 001 1h2a1 1 0 001-1v-2a1 1 0 011-1h2a1 1 0 011 1v2a1 1 0 001 1h2a1 1 0 001-1v-6.586l.293.293a1 1 0 001.414-1.414l-7-7z" />
  </svg>
);

const BookIcon = ({ className }) => (
  <svg className={className} fill="currentColor" viewBox="0 0 20 20">
    <path d="M9 4.804A7.968 7.968 0 005.5 4c-1.255 0-2.443.29-3.5.804v10A7.969 7.969 0 015.5 14c1.669 0 3.218.51 4.5 1.385A7.962 7.962 0 0114.5 14c1.255 0 2.443.29 3.5.804v-10A7.968 7.968 0 0014.5 4c-1.255 0-2.443.29-3.5.804V12a1 1 0 11-2 0V4.804z" />
  </svg>
);

const UserGroupIcon = ({ className }) => (
  <svg className={className} fill="currentColor" viewBox="0 0 20 20">
    <path d="M13 6a3 3 0 11-6 0 3 3 0 016 0zM18 8a2 2 0 11-4 0 2 2 0 014 0zM14 15a4 4 0 00-8 0v3h8v-3z" />
  </svg>
);

const CalendarIcon = ({ className }) => (
  <svg className={className} fill="currentColor" viewBox="0 0 20 20">
    <path fillRule="evenodd" d="M6 2a1 1 0 00-1 1v1H4a2 2 0 00-2 2v10a2 2 0 002 2h12a2 2 0 002-2V6a2 2 0 00-2-2h-1V3a1 1 0 10-2 0v1H7V3a1 1 0 00-1-1zm0 5a1 1 0 000 2h8a1 1 0 100-2H6z" clipRule="evenodd" />
  </svg>
);

const ExclamationIcon = ({ className }) => (
  <svg className={className} fill="currentColor" viewBox="0 0 20 20">
    <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
  </svg>
);

const UserIcon = ({ className }) => (
  <svg className={className} fill="currentColor" viewBox="0 0 20 20">
    <path fillRule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clipRule="evenodd" />
  </svg>
);

const CogIcon = ({ className }) => (
  <svg className={className} fill="currentColor" viewBox="0 0 20 20">
    <path fillRule="evenodd" d="M11.49 3.17c-.38-1.56-2.6-1.56-2.98 0a1.532 1.532 0 01-2.286.948c-1.372-.836-2.942.734-2.106 2.106.54.886.061 2.042-.947 2.287-1.561.379-1.561 2.6 0 2.978a1.532 1.532 0 01.947 2.287c-.836 1.372.734 2.942 2.106 2.106a1.532 1.532 0 012.287.947c.379 1.561 2.6 1.561 2.978 0a1.533 1.533 0 012.287-.947c1.372.836 2.942-.734 2.106-2.106a1.533 1.533 0 01.947-2.287c1.561-.379 1.561-2.6 0-2.978a1.532 1.532 0 01-.947-2.287c.836-1.372-.734-2.942-2.106-2.106a1.532 1.532 0 01-2.287-.947zM10 13a3 3 0 100-6 3 3 0 000 6z" clipRule="evenodd" />
  </svg>
);

