import React, { useState, useContext } from 'react';
import { EventContext } from './EventContext';
import './AdminDashboard.css';
import { Box, Container, Typography, Button, Grid } from '@mui/material';

const ManageEvents = () => {
  const { events, addEvent } = useContext(EventContext);
  const [eventData, setEventData] = useState({
    name: '',
    date: '',
    location: '',
    description: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEventData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!eventData.name || !eventData.date || !eventData.location || !eventData.description) {
      alert('Please fill in all fields');
      return;
    }
    addEvent(eventData);
    setEventData({ name: '', date: '', location: '', description: '' });
  };

  return (
    <Box sx={{
      minHeight: '100vh',
      background: `linear-gradient(135deg, #e0eafc 0%, #e3eafc 100%)`,
      py: 6,
      position: 'relative',
      '::before': {
        content: '""',
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        background: 'url(https://www.transparenttextures.com/patterns/cubes.png)',
        opacity: 0.08,
        zIndex: 0,
      }
    }}>
      {/* Hero Section */}
      <Container maxWidth="lg" sx={{ mb: 6, position: 'relative', zIndex: 1 }}>
        <Box sx={{
          borderRadius: 5,
          boxShadow: 6,
          background: 'rgba(255,255,255,0.98)',
          px: { xs: 2, md: 6 },
          py: { xs: 3, md: 5 },
          mb: 4,
          textAlign: 'center',
        }}>
          <Typography variant="h3" component="h1" gutterBottom sx={{ fontWeight: 800, color: '#1a237e', letterSpacing: 1 }}>
            🎉 Manage Events
          </Typography>
          <Typography variant="h6" color="text.secondary" sx={{ mb: 1, fontWeight: 500 }}>
            Create, view, and manage all campus events in one place
          </Typography>
        </Box>
        {/* Event Form */}
        <Box sx={{ background: 'linear-gradient(120deg, #f8fafc 60%, #e3eafc 100%)', borderRadius: 4, boxShadow: 3, p: 4, mb: 6 }}>
          <Typography variant="h5" gutterBottom sx={{ fontWeight: 700, color: '#3949ab', mb: 2 }}>
            Create New Event
          </Typography>
          <form onSubmit={handleSubmit} className="modal-form">
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6} md={3}>
                <input
                  type="text"
                  name="name"
                  value={eventData.name}
                  onChange={handleInputChange}
                  required
                  className="form-input"
                  placeholder="Event Name"
                  style={{ width: '100%', padding: 12, borderRadius: 8, border: '1px solid #e3eafc', fontSize: 16 }}
                />
              </Grid>
              <Grid item xs={12} sm={6} md={3}>
                <input
                  type="date"
                  name="date"
                  value={eventData.date}
                  onChange={handleInputChange}
                  required
                  className="form-input"
                  style={{ width: '100%', padding: 12, borderRadius: 8, border: '1px solid #e3eafc', fontSize: 16 }}
                />
              </Grid>
              <Grid item xs={12} sm={6} md={3}>
                <input
                  type="text"
                  name="location"
                  value={eventData.location}
                  onChange={handleInputChange}
                  required
                  className="form-input"
                  placeholder="Location"
                  style={{ width: '100%', padding: 12, borderRadius: 8, border: '1px solid #e3eafc', fontSize: 16 }}
                />
              </Grid>
              <Grid item xs={12} sm={6} md={3}>
                <input
                  type="text"
                  name="description"
                  value={eventData.description}
                  onChange={handleInputChange}
                  required
                  className="form-input"
                  placeholder="Description"
                  style={{ width: '100%', padding: 12, borderRadius: 8, border: '1px solid #e3eafc', fontSize: 16 }}
                />
              </Grid>
              <Grid item xs={12}>
                <Button
                  type="submit"
                  variant="contained"
                  color="primary"
                  className="submit-button gradient-primary"
                  sx={{ fontWeight: 700, fontSize: 18, px: 4, borderRadius: 3 }}
                >
                  Create Event
                </Button>
              </Grid>
            </Grid>
          </form>
        </Box>
      </Container>
      {/* Professional Event List for Admins */}
      <Container maxWidth="lg" sx={{ mt: 2, position: 'relative', zIndex: 1 }}>
        <Typography variant="h4" gutterBottom sx={{ fontWeight: 800, color: '#1976d2', mb: 4, letterSpacing: 0.5 }}>
          All Posted Events
        </Typography>
        {events.length === 0 ? (
          <Typography color="text.secondary" sx={{ fontSize: 18, textAlign: 'center', mt: 2 }}>
            No events have been posted yet.
          </Typography>
        ) : (
          <Grid container spacing={4}>
            {[...events].reverse().map((event) => (
              <Grid item xs={12} sm={6} md={4} key={event.id}>
                <Box
                  sx={{
                    borderRadius: 5,
                    boxShadow: 6,
                    background: 'linear-gradient(120deg, #f8fafc 60%, #e3eafc 100%)',
                    transition: 'transform 0.2s',
                    p: 4,
                    mb: 2,
                    border: '1px solid #e3eafc',
                    minHeight: 210,
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'flex-start',
                    position: 'relative',
                    overflow: 'visible',
                    '&:hover': {
                      transform: 'translateY(-8px) scale(1.03)',
                      boxShadow: 10,
                    }
                  }}
                >
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                    <Box sx={{
                      width: 44,
                      height: 44,
                      borderRadius: '50%',
                      background: 'linear-gradient(135deg, #1976d2 60%, #42a5f5 100%)',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      mr: 2,
                      boxShadow: 3,
                    }}>
                      <span role="img" aria-label="event" style={{ fontSize: 26 }}>📢</span>
                    </Box>
                    <Typography variant="h5" sx={{ fontWeight: 700, color: '#1976d2', mb: 0, letterSpacing: 0.5 }}>
                      {event.name}
                    </Typography>
                  </Box>
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                    <Box sx={{
                      background: 'linear-gradient(90deg, #42a5f5 60%, #1976d2 100%)',
                      color: '#fff',
                      borderRadius: 2,
                      px: 1.5,
                      py: 0.5,
                      fontWeight: 700,
                      fontSize: 15,
                      mr: 1.5,
                      minWidth: 90,
                      textAlign: 'center',
                      letterSpacing: 0.5,
                      boxShadow: 1,
                    }}>
                      {event.date ? new Date(event.date).toLocaleDateString() : ''}
                    </Box>
                    <Typography color="text.secondary" sx={{ fontSize: 15 }}>
                      {event.location}
                    </Typography>
                  </Box>
                  <Typography color="text.secondary" sx={{ mb: 1, fontSize: 15, minHeight: 40 }}>
                    <strong>Description:</strong> {event.description}
                  </Typography>
                </Box>
              </Grid>
            ))}
          </Grid>
        )}
      </Container>
    </Box>
  );
};

export default ManageEvents;
