import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const StudentLogin = () => {
  const [formData, setFormData] = useState({
    rollNumber: '',
    password: '',
    semester: '',
    role: 'student'
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate form data
    if (!formData.rollNumber || !formData.password || !formData.semester) {
      alert('Please fill in all required fields');
      return;
    }

    // Simulate login (replace with actual API call)
    const validRollNumbers = ['23154', '24154', '25154']; // Example valid roll numbers
    if (validRollNumbers.includes(formData.rollNumber)) {
      // Store user data in localStorage (for demo purposes)
      localStorage.setItem('user', JSON.stringify({
        role: 'student',
        rollNumber: formData.rollNumber,
        semester: formData.semester
      }));

      // Navigate to student dashboard
      navigate('/dashboard-student');
    } else {
      alert('Invalid roll number or password');
    }
  };

  return (
    <div className="student-login">
      <h2>Student Login</h2>
      <form onSubmit={handleSubmit} className="login-form">
        <div className="form-group">
          <label htmlFor="student-roll">Roll Number</label>
          <input
            type="text"
            id="student-roll"
            name="rollNumber"
            value={formData.rollNumber}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="student-password">Password</label>
          <input
            type="password"
            id="student-password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="student-semester">Semester</label>
          <select
            id="student-semester"
            name="semester"
            value={formData.semester}
            onChange={handleChange}
            required
          >
            <option value="">Select Semester</option>
            <option value="1">1st Semester</option>
            <option value="2">2nd Semester</option>
            <option value="3">3rd Semester</option>
            <option value="4">4th Semester</option>
            <option value="5">5th Semester</option>
            <option value="6">6th Semester</option>
            <option value="7">7th Semester</option>
            <option value="8">8th Semester</option>
          </select>
        </div>
        <button type="submit" className="login-btn">Student Login</button>
      </form>
    </div>
  );
};

export default StudentLogin;
