import React from 'react';


const AdminDashboard = () => {
  return (
    <div className="min-h-screen flex overflow-hidden">
      {/* Sidebar */}
      <div className="w-64 bg-white shadow-lg hidden lg:block">
        <div className="p-6">
          <h2 className="text-2xl font-bold text-blue-600">EduAdmin</h2>
        </div>
        <nav className="mt-6 space-y-1">
          {/* Sidebar Navigation Items */}
          <SidebarItem icon={HomeIcon} label="Dashboard" active />
          <SidebarItem icon={BookIcon} label="Courses" />
          <SidebarItem icon={UserGroupIcon} label="Students" />
          <SidebarItem icon={CalendarIcon} label="Events" />
          <SidebarItem icon={ExclamationIcon} label="Complaints" badge="3" />
          <SidebarItem icon={UserIcon} label="Profile" />
          <SidebarItem icon={CogIcon} label="Settings" />
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 overflow-auto">
        {/* Header */}
        <Header />

        {/* Welcome Banner */}
        <div className="bg-gradient-to-r from-blue-500 to-indigo-600 text-white">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
            <h2 className="text-2xl font-bold">Welcome, Admin!</h2>
            <p className="mt-1 text-blue-100">Here's what's happening with your institution today.</p>
          </div>
        </div>

        {/* Dashboard Content */}
        <DashboardContent />
      </div>
    </div>
  );
};

const SidebarItem = ({ icon: Icon, label, active = false, badge }) => (
  <a
    href="#"
    className={`relative nav-item flex items-center px-6 py-3 text-gray-600 hover:bg-blue-50 hover:text-gray-700 ${
      active ? 'bg-blue-50 text-gray-700 border-l-4 border-blue-500' : ''
    }`}
  >
    <Icon className="w-5 h-5 mr-3" />
    <span className="text-sm font-medium">{label}</span>
    {badge && (
      <span className="notification-badge absolute top-1 right-4 w-5 h-5 bg-red-500 text-white text-xs flex items-center justify-center rounded-full">
        {badge}
      </span>
    )}
  </a>
);

const Header = () => (
  <header className="bg-white shadow-sm sticky top-0 z-10">
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex items-center justify-between">
      <div className="flex items-center">
        <button id="mobile-menu-button" className="lg:hidden mr-4 text-gray-500">
          <MenuIcon className="w-5 h-5" />
        </button>
        <h1 className="text-lg font-semibold text-gray-800">Admin Dashboard</h1>
      </div>
      <div className="flex items-center space-x-4">
        <NotificationIcon />
        <ProfileIcon />
      </div>
    </div>
  </header>
);

const NotificationIcon = () => (
  <button className="text-gray-500 hover:text-gray-700">
    <BellIcon className="w-5 h-5" />
  </button>
);

const ProfileIcon = () => (
  <div className="relative">
    <img
      className="h-8 w-8 rounded-full object-cover"
      src="https://via.placeholder.com/32"
      alt="User profile"
    />
  </div>
);

const DashboardContent = () => (
  <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      {/* Cards */}
      <DashboardCard title="Manage Courses" desc="Add, edit, and manage courses" link="#" />
      <DashboardCard title="Manage Events" desc="Create and manage campus events" link="#" />
      <DashboardCard title="Quick Actions" desc="Manage common tasks" link="#" isQuick />
      <DashboardCard title="Manage Complaints" desc="View and resolve student complaints" link="#" />
      <DashboardCard title="Student Registration" desc="Register new students to the system" link="#" />
      <DashboardCard title="Reports & Analytics" desc="View detailed reports and statistics" link="#" />
    </div>
    {/* Stats Section */}
    <QuickStats />
  </div>
);

const DashboardCard = ({ title, desc, link, isQuick }) => (
  <div className="card bg-white rounded-lg shadow-md overflow-hidden p-6 transition duration-200 ease-in-out transform hover:-translate-y-1 hover:shadow-xl">
    <h3 className="text-lg font-semibold text-gray-800 mb-2">{title}</h3>
    <p className="text-sm text-gray-600 mb-4">{desc}</p>
    {isQuick ? (
      <div className="flex flex-wrap gap-2">
        <a href="#" className="inline-block px-3 py-2 bg-gray-100 text-gray-700 text-sm font-medium rounded-md hover:bg-gray-200">
          View Announcements
        </a>
        <a
          href="#"
          className="inline-flex items-center px-3 py-2 bg-gray-100 text-gray-700 text-sm font-medium rounded-md hover:bg-gray-200"
        >
          Register Student
        </a>
      </div>
    ) : (
      <a
        href={link}
        className="inline-block px-4 py-2 bg-blue-600 text-white text-sm font-medium rounded-md hover:bg-blue-700 transition-colors"
      >
        {title}
      </a>
    )}
  </div>
);

const QuickStats = () => (
  <div className="mt-8">
