import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Box, Container, TextField, Button, Typography, Alert } from '@mui/material';

const StudentLogin = () => {
  const [formData, setFormData] = useState({
    rollNumber: '',
    password: '',
    role: 'student',
  });
  const [error, setError] = useState(''); // Added for error feedback
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    console.log(`Updating ${name} to: ${value}`); // Debug log
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
    setError(''); // Clear error on input change
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData); // Debug log

    // Validate form data
    if (!formData.rollNumber || !formData.password) {
      setError('Please fill in all required fields');
      return;
    }

    // Check credentials against registered students in localStorage
    const students = JSON.parse(localStorage.getItem('students') || '[]');
    const student = students.find(
      (s) => s.rollNumber === formData.rollNumber && s.password === formData.password
    );
    if (student) {
      localStorage.setItem(
        'user',
        JSON.stringify({
          role: 'student',
          rollNumber: student.rollNumber,
          semester: student.semester,
          name: student.name,
        })
      );
      navigate('/dashboard/student');
    } else {
      setError('Invalid roll number or password');
    }
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#f1f5f9', // Match AdminDashboard.css background
      }}
    >
      <Container maxWidth="sm">
        <Box
          sx={{
            p: 4,
            backgroundColor: '#fff',
            borderRadius: 2,
            boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)', // Match AdminDashboard.css card shadow
          }}
        >
          <Typography
            variant="h4"
            component="h1"
            gutterBottom
            sx={{ fontWeight: 600, color: '#1a1a1a' }} // Match AdminDashboard typography
          >
            Student Login
          </Typography>
          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}
          <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3 }}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="rollNumber"
              label="Roll Number"
              name="rollNumber"
              autoComplete="username"
              autoFocus
              value={formData.rollNumber}
              onChange={handleChange}
              error={!!error && !formData.rollNumber}
              helperText={error && !formData.rollNumber ? 'Roll Number is required' : ''}
              sx={{
                '& .MuiInputBase-input': {
                  fontSize: '0.875rem',
                  padding: '8px 12px',
                },
                '& .MuiOutlinedInput-root': {
                  borderRadius: '6px',
                },
              }}
            />
            <TextField
              margin="normal"
              required
              fullWidth
              id="password"
              label="Password"
              name="password"
              type="password"
              autoComplete="current-password"
              value={formData.password}
              onChange={handleChange}
              error={!!error && !formData.password}
              helperText={error && !formData.password ? 'Password is required' : ''}
              sx={{
                '& .MuiInputBase-input': {
                  fontSize: '0.875rem',
                  padding: '8px 12px',
                },
                '& .MuiOutlinedInput-root': {
                  borderRadius: '6px',
                },
              }}
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{
                mt: 3,
                mb: 2,
                background: 'linear-gradient(135deg, #1976d2 0%, #42a5f5 100%)', // Match AdminDashboard gradient-primary
                color: '#ffffff',
                textTransform: 'none',
                fontWeight: 600,
                padding: '8px 16px',
                borderRadius: '6px',
                '&:hover': {
                  background: 'linear-gradient(135deg, #1565c0 0%, #2196f3 100%)',
                },
              }}
            >
              Student Login
            </Button>
          </Box>
        </Box>
      </Container>
    </Box>
  );
};

export default StudentLogin;
