import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AdminLogin = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    department: '',
    role: 'admin'
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate form data
    if (!formData.username || !formData.password || !formData.department) {
      alert('Please fill in all required fields');
      return;
    }

    // Simulate login (replace with actual API call)
    const validAdmins = {
      'admin': 'admin123',
      'superadmin': 'super123'
    };

    if (validAdmins[formData.username] === formData.password) {
      // Store admin data in localStorage (for demo purposes)
      localStorage.setItem('user', JSON.stringify({
        role: 'admin',
        username: formData.username,
        department: formData.department
      }));

      // Navigate to admin dashboard
      navigate('/dashboard-admin');
    } else {
      alert('Invalid username or password');
    }
  };

  return (
    <div className="admin-login">
      <h2>Admin Login</h2>
      <form onSubmit={handleSubmit} className="login-form">
        <div className="form-group">
          <label htmlFor="admin-username">Username</label>
          <input
            type="text"
            id="admin-username"
            name="username"
            value={formData.username}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="admin-password">Password</label>
          <input
            type="password"
            id="admin-password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="admin-department">Department</label>
          <select
            id="admin-department"
            name="department"
            value={formData.department}
            onChange={handleChange}
            required
          >
            <option value="">Select Department</option>
            <option value="IT">Information Technology</option>
            <option value="CSE">Computer Science</option>
            <option value="ECE">Electronics & Communication</option>
            <option value="MECH">Mechanical Engineering</option>
          </select>
        </div>
        <button type="submit" className="login-btn">Admin Login</button>
      </form>
    </div>
  );
};

export default AdminLogin;
