import React, { useContext } from 'react';
import { EventContext } from './EventContext';
import { Box, Container, Typography, Grid, Card, CardContent } from '@mui/material';

const StudentProfile = () => {
  const { events } = useContext(EventContext);
  // Show all events (no date filter)
  const allEvents = events;

  return (
    <Box sx={{ minHeight: '100vh', background: 'linear-gradient(135deg, #e0eafc 0%, #cfdef3 100%)', py: 6 }}>
      <Container maxWidth="lg">
        <Box sx={{
          borderRadius: 5,
          boxShadow: 6,
          background: 'rgba(255,255,255,0.98)',
          px: { xs: 2, md: 6 },
          py: { xs: 3, md: 5 },
          mb: 4,
          textAlign: 'center',
        }}>
          <Typography variant="h3" component="h1" gutterBottom sx={{ fontWeight: 800, color: '#1a237e', letterSpacing: 1 }}>
            ðŸ‘¤ Student Profile
          </Typography>
          <Typography variant="h6" color="text.secondary" sx={{ mb: 1, fontWeight: 500 }}>
            View your upcoming events
          </Typography>
        </Box>
        <Box sx={{ mb: 3 }}>
          <Typography variant="h4" gutterBottom sx={{ fontWeight: 700, color: '#3949ab', letterSpacing: 0.5 }}>
            ðŸ“… Upcoming Events
          </Typography>
        </Box>
        {allEvents.length === 0 ? (
          <Typography variant="body1" color="text.secondary" sx={{ fontSize: 18, textAlign: 'center', mt: 4 }}>
            No events available.
          </Typography>
        ) : (
          <Grid container spacing={4}>
            {[...allEvents].reverse().map((event) => (
              <Grid item xs={12} sm={6} md={4} key={event.id}>
                <Card sx={{
                  borderRadius: 4,
                  boxShadow: 4,
                  background: 'linear-gradient(120deg, #f8fafc 60%, #e3eafc 100%)',
                  transition: 'transform 0.2s',
                  '&:hover': { transform: 'translateY(-8px) scale(1.03)', boxShadow: 8 },
                }}>
                  <CardContent sx={{ p: 3 }}>
                    <Typography variant="h5" gutterBottom sx={{ fontWeight: 700, color: '#1976d2', mb: 1 }}>
                      {event.name}
                    </Typography>
                    <Typography color="text.secondary" sx={{ mb: 1, fontSize: 15 }}>
                      <strong>Date:</strong> {event.date ? new Date(event.date).toLocaleDateString() : ''}
                    </Typography>
                    <Typography color="text.secondary" sx={{ mb: 1, fontSize: 15 }}>
                      <strong>Description:</strong> {event.description}
                    </Typography>
                    <Typography color="text.secondary" sx={{ fontSize: 15 }}>
                      <strong>Location:</strong> {event.location}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}
      </Container>
    </Box>
  );
};

export default StudentProfile;
