import React, { useState, useEffect } from 'react';
import { Calendar, Clock, Plus, Edit2, Trash2, BookOpen, Users, Settings, Bell, Search, Menu, X, Sparkles, CheckCircle, Zap } from 'lucide-react';

const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'];
const timeSlots = ['08:00-09:30', '09:30-11:00', '11:00-12:30', '12:30-14:00', '14:00-15:30', '15:30-17:00'];

const defaultTimetable = Array(days.length).fill().map(() => Array(timeSlots.length).fill({ subject: '' }));

const getInitialData = () => {
  const saved = JSON.parse(localStorage.getItem('studentTimetableV2') || '{}');
  return { tasksByDate: {}, timetableByDate: {}, ...saved };
};

const Navbar = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  return (
    <nav className="bg-gradient-to-r from-indigo-600 via-purple-600 to-blue-600 shadow-xl backdrop-blur-lg border-b border-white/10">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <div className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-white/20 rounded-xl flex items-center justify-center backdrop-blur-sm">
              <BookOpen className="w-6 h-6 text-white" />
            </div>
            <span className="text-2xl font-bold text-white tracking-tight">CampusLink</span>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-8">
            <a href="#" className="text-white/90 hover:text-white transition-colors font-medium">Dashboard</a>
            <a href="#" className="text-white/90 hover:text-white transition-colors font-medium">Courses</a>
            <a href="#" className="text-white font-medium bg-white/20 px-4 py-2 rounded-lg backdrop-blur-sm hover:bg-white/30 transition-all">Timetable</a>
            <a href="#" className="text-white/90 hover:text-white transition-colors font-medium">Assignments</a>
            <div className="flex items-center space-x-4">
              <button className="text-white/90 hover:text-white transition-colors">
                <Search className="w-5 h-5" />
              </button>
              <button className="text-white/90 hover:text-white transition-colors">
                <Bell className="w-5 h-5" />
              </button>
              <button className="text-white/90 hover:text-white transition-colors">
                <Settings className="w-5 h-5" />
              </button>
            </div>
          </div>

          {/* Mobile menu button */}
          <button
            className="md:hidden text-white p-2"
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          >
            {isMobileMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
          </button>
        </div>

        {/* Mobile Navigation */}
        {isMobileMenuOpen && (
          <div className="md:hidden bg-white/10 backdrop-blur-lg rounded-lg m-4 p-4">
            <div className="flex flex-col space-y-3">
              <a href="#" className="text-white/90 hover:text-white transition-colors font-medium py-2">Dashboard</a>
              <a href="#" className="text-white/90 hover:text-white transition-colors font-medium py-2">Courses</a>
              <a href="#" className="text-white font-medium bg-white/20 px-4 py-2 rounded-lg backdrop-blur-sm">Timetable</a>
              <a href="#" className="text-white/90 hover:text-white transition-colors font-medium py-2">Assignments</a>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

const LoadingSpinner = () => (
  <div className="flex items-center justify-center p-8">
    <div className="relative">
      <div className="w-16 h-16 border-4 border-purple-200 border-t-purple-600 rounded-full animate-spin"></div>
      <Sparkles className="w-6 h-6 text-purple-600 absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2" />
    </div>
  </div>
);

const GeneratingAnimation = ({ onComplete }) => {
  const [stage, setStage] = useState(0);
  const stages = [
    "Analyzing your tasks...",
    "Optimizing schedule conflicts...",
    "Balancing workload distribution...",
    "Finalizing your perfect timetable..."
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setStage(prev => {
        if (prev < stages.length - 1) {
          return prev + 1;
        } else {
          setTimeout(onComplete, 1000);
          return prev;
        }
      });
    }, 1500);

    return () => clearInterval(timer);
  }, [onComplete]);

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50">
      <div className="bg-white rounded-2xl p-8 max-w-md mx-4 text-center shadow-2xl">
        <div className="w-20 h-20 mx-auto mb-6 relative">
          <div className="w-20 h-20 border-4 border-purple-200 border-t-purple-600 rounded-full animate-spin"></div>
          <Zap className="w-8 h-8 text-purple-600 absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 animate-pulse" />
        </div>
        <h3 className="text-xl font-bold text-gray-800 mb-4">Creating Your Smart Timetable</h3>
        <div className="space-y-3">
          {stages.map((stageText, index) => (
            <div key={index} className={`flex items-center space-x-3 transition-all duration-500 ${
              index <= stage ? 'opacity-100' : 'opacity-40'
            }`}>
              {index < stage ? (
                <CheckCircle className="w-5 h-5 text-green-500 flex-shrink-0" />
              ) : index === stage ? (
                <div className="w-5 h-5 border-2 border-purple-600 border-t-transparent rounded-full animate-spin flex-shrink-0"></div>
              ) : (
                <div className="w-5 h-5 border-2 border-gray-300 rounded-full flex-shrink-0"></div>
              )}
              <span className={`text-sm ${index <= stage ? 'text-gray-800 font-medium' : 'text-gray-500'}`}>
                {stageText}
              </span>
            </div>
          ))}
        </div>
        <div className="mt-6 bg-gray-200 rounded-full h-2 overflow-hidden">
          <div 
            className="h-full bg-gradient-to-r from-purple-500 to-blue-500 transition-all duration-1000 ease-out"
            style={{ width: `${((stage + 1) / stages.length) * 100}%` }}
          ></div>
        </div>
      </div>
    </div>
  );
};

const Timetable = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [taskForm, setTaskForm] = useState({ name: '', duration: 1 });
  const [step, setStep] = useState('input');
  const [editCell, setEditCell] = useState(null);
  const [form, setForm] = useState({ subject: '' });
  const [data, setData] = useState(getInitialData());
  const [isGenerating, setIsGenerating] = useState(false);

  const dateKey = selectedDate.toISOString().split('T')[0];
  const tasks = data.tasksByDate[dateKey] || [];
  const timetable = data.timetableByDate[dateKey] || defaultTimetable;

  useEffect(() => {
    localStorage.setItem('studentTimetableV2', JSON.stringify(data));
  }, [data]);

  const formatDate = (date) => {
    return date.toLocaleDateString('en-US', { 
      weekday: 'long', 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    });
  };

  const handleEdit = (dayIndex, timeIndex) => {
    setEditCell({ dayIndex, timeIndex });
    setForm({ subject: timetable[dayIndex][timeIndex].subject });
  };

  const handleDelete = (dayIndex, timeIndex) => {
    const updated = timetable.map((row, d) =>
      row.map((cell, t) => (d === dayIndex && t === timeIndex ? { subject: '' } : cell))
    );
    setData(prev => ({
      ...prev,
      timetableByDate: { ...prev.timetableByDate, [dateKey]: updated }
    }));
  };

  const handleDialogClose = () => {
    setEditCell(null);
    setForm({ subject: '' });
  };

  const handleSave = () => {
    const updated = timetable.map((row, d) =>
      row.map((cell, t) => (d === editCell.dayIndex && t === editCell.timeIndex ? { subject: form.subject } : cell))
    );
    setData(prev => ({
      ...prev,
      timetableByDate: { ...prev.timetableByDate, [dateKey]: updated }
    }));
    handleDialogClose();
  };

  const handleAddTask = () => {
    if (!taskForm.name.trim() || !taskForm.duration) return;
    setData(prev => {
      const prevTasks = prev.tasksByDate[dateKey] || [];
      return {
        ...prev,
        tasksByDate: {
          ...prev.tasksByDate,
          [dateKey]: [...prevTasks, { name: taskForm.name.trim(), duration: Number(taskForm.duration) }]
        }
      };
    });
    setTaskForm({ name: '', duration: 1 });
  };

  const handleDeleteTask = (idx) => {
    setData(prev => {
      const prevTasks = prev.tasksByDate[dateKey] || [];
      return {
        ...prev,
        tasksByDate: {
          ...prev.tasksByDate,
          [dateKey]: prevTasks.filter((_, i) => i !== idx)
        }
      };
    });
  };

  const handleGenerateTimetable = () => {
    setIsGenerating(true);
  };

  const completeGeneration = () => {
    let flatSlots = [];
    for (let d = 0; d < days.length; d++) {
      for (let t = 0; t < timeSlots.length; t++) {
        flatSlots.push({ d, t });
      }
    }
    let slotIdx = 0;
    const newTable = Array(days.length).fill().map(() => Array(timeSlots.length).fill({ subject: '' }));
    for (const task of tasks) {
      for (let i = 0; i < task.duration && slotIdx < flatSlots.length; i++) {
        const { d, t } = flatSlots[slotIdx++];
        newTable[d][t] = { subject: task.name };
      }
    }
    setData(prev => ({
      ...prev,
      timetableByDate: { ...prev.timetableByDate, [dateKey]: newTable }
    }));
    setIsGenerating(false);
    setStep('timetable');
  };

  const subjectColors = {
    'Mathematics': 'bg-blue-100 text-blue-800 border-blue-200',
    'Physics': 'bg-purple-100 text-purple-800 border-purple-200',
    'Chemistry': 'bg-green-100 text-green-800 border-green-200',
    'Computer Science': 'bg-indigo-100 text-indigo-800 border-indigo-200',
    'Electronics': 'bg-yellow-100 text-yellow-800 border-yellow-200',
    'Lunch Break': 'bg-orange-100 text-orange-800 border-orange-200'
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      <Navbar />

      {isGenerating && <GeneratingAnimation onComplete={completeGeneration} />}

      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-5xl font-bold bg-gradient-to-r from-indigo-600 via-purple-600 to-blue-600 bg-clip-text text-transparent mb-4">
            Smart Timetable Generator
          </h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            Create optimized schedules that adapt to your learning style and maximize productivity
          </p>
        </div>

        <div className="grid lg:grid-cols-12 gap-8">
          {/* Date Picker Card */}
          <div className="lg:col-span-4">
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-xl p-6 border border-white/20">
              <div className="flex items-center space-x-3 mb-6">
                <div className="w-10 h-10 bg-gradient-to-r from-indigo-500 to-purple-600 rounded-xl flex items-center justify-center">
                  <Calendar className="w-5 h-5 text-white" />
                </div>
                <h2 className="text-xl font-bold text-gray-800">Select Date</h2>
              </div>

              <div className="bg-gradient-to-br from-indigo-50 to-purple-50 rounded-xl p-4 mb-4">
                <p className="text-sm text-gray-600 mb-2">Selected Date</p>
                <p className="text-lg font-semibold text-gray-800">{formatDate(selectedDate)}</p>
              </div>

              <input
                type="date"
                value={dateKey}
                onChange={(e) => setSelectedDate(new Date(e.target.value))}
                className="w-full p-3 border border-gray-200 rounded-xl focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all"
              />
            </div>
          </div>

          {/* Main Content */}
          <div className="lg:col-span-8">
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20 overflow-hidden">
              {step === 'input' && (
                <div className="p-8">
                  <div className="flex items-center space-x-3 mb-8">
                    <div className="w-10 h-10 bg-gradient-to-r from-green-500 to-teal-600 rounded-xl flex items-center justify-center">
                      <Plus className="w-5 h-5 text-white" />
                    </div>
                    <h2 className="text-2xl font-bold text-gray-800">Manage Tasks</h2>
                  </div>

                  {/* Task Input Form */}
                  <div className="bg-gradient-to-r from-gray-50 to-blue-50 rounded-xl p-6 mb-8">
                    <div className="grid md:grid-cols-12 gap-4 items-end">
                      <div className="md:col-span-6">
                        <label className="block text-sm font-medium text-gray-700 mb-2">Task Name</label>
                        <input
                          type="text"
                          value={taskForm.name}
                          onChange={(e) => setTaskForm({ ...taskForm, name: e.target.value })}
                          placeholder="Enter task name..."
                          className="w-full p-3 border border-gray-200 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all"
                        />
                      </div>
                      <div className="md:col-span-3">
                        <label className="block text-sm font-medium text-gray-700 mb-2">Duration (slots)</label>
                        <input
                          type="number"
                          value={taskForm.duration}
                          onChange={(e) => setTaskForm({ ...taskForm, duration: e.target.value })}
                          min="1"
                          max={days.length * timeSlots.length}
                          className="w-full p-3 border border-gray-200 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all"
                        />
                      </div>
                      <div className="md:col-span-3">
                        <button
                          onClick={handleAddTask}
                          className="w-full bg-gradient-to-r from-indigo-600 to-purple-600 text-white px-6 py-3 rounded-lg font-semibold hover:from-indigo-700 hover:to-purple-700 transition-all duration-200 transform hover:scale-105 shadow-lg"
                        >
                          Add Task
                        </button>
                      </div>
                    </div>
                  </div>

                  {/* Tasks List */}
                  {tasks.length > 0 && (
                    <div className="mb-8">
                      <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center">
                        <Clock className="w-5 h-5 mr-2 text-indigo-600" />
                        Tasks to Schedule ({tasks.length})
                      </h3>
                      <div className="grid md:grid-cols-2 gap-4">
                        {tasks.map((task, idx) => (
                          <div key={idx} className="bg-white rounded-lg p-4 border border-gray-200 shadow-sm hover:shadow-md transition-all">
                            <div className="flex items-center justify-between">
                              <div>
                                <h4 className="font-semibold text-gray-800">{task.name}</h4>
                                <p className="text-sm text-gray-600">{task.duration} slot{task.duration > 1 ? 's' : ''}</p>
                              </div>
                              <button
                                onClick={() => handleDeleteTask(idx)}
                                className="text-red-500 hover:text-red-700 p-2 rounded-lg hover:bg-red-50 transition-all"
                              >
                                <Trash2 className="w-4 h-4" />
                              </button>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Generate Button */}
                  <div className="text-center">
                    <button
                      onClick={handleGenerateTimetable}
                      disabled={tasks.length === 0}
                      className="bg-gradient-to-r from-green-600 to-teal-600 text-white px-12 py-4 rounded-xl font-bold text-lg hover:from-green-700 hover:to-teal-700 transition-all duration-200 transform hover:scale-105 shadow-xl disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none flex items-center space-x-3 mx-auto"
                    >
                      <Sparkles className="w-6 h-6" />
                      <span>Generate Smart Timetable</span>
                    </button>
                  </div>
                </div>
              )}

              {step === 'timetable' && (
                <div className="p-8">
                  <div className="flex items-center justify-between mb-8">
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-xl flex items-center justify-center">
                        <Calendar className="w-5 h-5 text-white" />
                      </div>
                      <h2 className="text-2xl font-bold text-gray-800">Your Timetable</h2>
                    </div>
                    <button
                      onClick={() => setStep('input')}
                      className="bg-gray-100 hover:bg-gray-200 text-gray-700 px-6 py-2 rounded-lg font-medium transition-all"
                    >
                      Back to Tasks
                    </button>
                  </div>

                  {/* Timetable Grid */}
                  <div className="overflow-x-auto">
                    <div className="min-w-full bg-white rounded-xl shadow-lg overflow-hidden border border-gray-200">
                      <div className="grid grid-cols-6 bg-gradient-to-r from-indigo-600 to-purple-600">
                        <div className="p-4 text-white font-semibold text-center">Time</div>
                        {days.map((day) => (
                          <div key={day} className="p-4 text-white font-semibold text-center">{day}</div>
                        ))}
                      </div>

                      {timeSlots.map((time, rowIndex) => (
                        <div key={rowIndex} className="grid grid-cols-6 border-b border-gray-200 last:border-b-0">
                          <div className="p-4 bg-gray-50 font-medium text-gray-700 text-center border-r border-gray-200">
                            {time}
                          </div>
                          {days.map((_, dayIndex) => {
                            const subject = timetable[dayIndex][rowIndex].subject;
                            const colorClass = subject ? (subjectColors[subject] || 'bg-gray-100 text-gray-800 border-gray-200') : 'bg-white';

                            return (
                              <div key={dayIndex} className={`p-4 border-r border-gray-200 last:border-r-0 min-h-[80px] ${colorClass} transition-all hover:shadow-sm`}>
                                <div className="flex flex-col items-center justify-center h-full space-y-2">
                                  <span className="text-sm font-medium text-center">
                                    {subject || 'Free Time'}
                                  </span>
                                  <div className="flex space-x-1">
                                    <button
                                      onClick={() => handleEdit(dayIndex, rowIndex)}
                                      className="p-1 text-gray-500 hover:text-indigo-600 hover:bg-indigo-50 rounded transition-all"
                                    >
                                      <Edit2 className="w-3 h-3" />
                                    </button>
                                    <button
                                      onClick={() => handleDelete(dayIndex, rowIndex)}
                                      className="p-1 text-gray-500 hover:text-red-600 hover:bg-red-50 rounded transition-all"
                                    >
                                      <Trash2 className="w-3 h-3" />
                                    </button>
                                  </div>
                                </div>
                              </div>
                            );
                          })}
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Edit Dialog */}
      {editCell && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl shadow-2xl max-w-md w-full">
            <div className="p-6 border-b border-gray-200">
              <h3 className="text-xl font-bold text-gray-800">Edit Schedule</h3>
            </div>
            <div className="p-6">
              <label className="block text-sm font-medium text-gray-700 mb-2">Subject</label>
              <select
                value={form.subject}
                onChange={(e) => setForm({ subject: e.target.value })}
                className="w-full p-3 border border-gray-200 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all"
              >
                <option value="">Free Time</option>
                <option value="Mathematics">Mathematics</option>
                <option value="Physics">Physics</option>
                <option value="Chemistry">Chemistry</option>
                <option value="Computer Science">Computer Science</option>
                <option value="Electronics">Electronics</option>
                <option value="Lunch Break">Lunch Break</option>
              </select>
            </div>
            <div className="p-6 border-t border-gray-200 flex space-x-3">
              <button
                onClick={handleDialogClose}
                className="flex-1 px-4 py-2 text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-lg font-medium transition-all"
              >
                Cancel
              </button>
              <button
                onClick={handleSave}
                className="flex-1 px-4 py-2 text-white bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 rounded-lg font-medium transition-all"
              >
                Save Changes
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Timetable;
