        padding: '1.1rem 2.5rem',
        fontSize: '1.5rem',
        fontWeight: 700,
        letterSpacing: '2px',
        boxShadow: '0 2px 12px rgba(102, 126, 234, 0.08)',
        position: 'sticky',
