import React from 'react';
import { Link } from 'react-router-dom';
import campusImage from '../assets/campus.jpg';

/* ---------- Inline Styles ---------- */
const styles = {
  homepageWrapper: { fontFamily: 'Arial, sans-serif' },

  heroSection: {
    backgroundImage: `url(${campusImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    height: '90vh',
    color: '#fff',
    display: 'flex',
    alignItems: 'center',
  },

  overlay: {
    backgroundColor: 'rgba(0,0,0,0.55)',
    width: '100%',
    padding: '4rem',
    boxSizing: 'border-box',
  },

  content: {
    maxWidth: 600,
  },

  title: {
    fontSize: '3rem',
    fontWeight: 'bold',
    margin: 0,
  },

  subtitle: {
    fontSize: '1.2rem',
    margin: '1rem 0 0',
  },

  buttons: {
    marginTop: '2rem',
  },

  btn: {
    padding: '0.8rem 2rem',
    marginRight: '1rem',
    backgroundColor: '#ff4d4f',
    color: '#fff',
    textDecoration: 'none',
    borderRadius: 8,
    fontSize: '1rem',
    border: 'none',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },

  introSection: {
    padding: '4rem 2rem',
    textAlign: 'center',
  },

  introText: {
    maxWidth: 800,
    margin: '1rem auto',
    fontSize: '1.1rem',
    lineHeight: 1.6,
    color: '#333',
  },
};

/* ---------- Component ---------- */
const HomePage = () => {
  return (
    <div style={styles.homepageWrapper}>
      {/* Hero */}
      <div style={styles.heroSection}>
        <div style={styles.overlay}>
          <div style={styles.content}>
            <h1 style={styles.title}>Inspiration and Innovation.</h1>
            <p style={styles.subtitle}>
              CampusLink is your centralized hub for student utilities. From
              announcements to complaint tracking, everything you need is one
              click away.
            </p>
            <div style={styles.buttons}>
              <Link to="/login" style={styles.btn}>
                Login
              </Link>
              <Link
                to="/register"
                style={{
                  ...styles.btn,
                  backgroundColor: '#0066cc',
                }}
              >
                Register
              </Link>
            </div>
          </div>
        </div>
      </div>

      {/* Intro */}
      <section style={styles.introSection}>
        <h2 style={{ marginTop: 0 }}>A Few Words About CampusLink</h2>
        <p style={styles.introText}>
          CampusLink simplifies campus life by centralizing critical utilities
          like class schedules, announcements, lost & found, and hostel
          complaints. It empowers students and eases the admin workload.
        </p>
      </section>
    </div>
  );
};

export default HomePage;
